 {
  //  console.log(event.target.value);
   setNewItem(event.target.value);
 };
 const itemAdd=(event)=>{
  preventDefault();
 };
return (
      <>
      <div className="main_div">
        <div className="center_div">
          <br/>
          <h1>Add Item's</h1>
          <br/>
          <input type="text" placeholder="type item here." onChange={itenmEvent}/>
          <button onClick={itemAdd}>+</button>

          <ol>
            {/* <li>{item}</li>
             */}
             {arItems.map((itemval)=>{
               return <li>{itemval}</li>
             })}
          </ol>
        </div>
      </div>

      </>
    );

};
export  default  App;
